prepare_comparisons_for_exp_GSE119291_wrapper <- function()
{
  ### PREPARE opts_comparisons_76 ###
  # 1) w obrębie jednego typu komórki i w obrébie jednej płytki
  # 1a) extract all GSMs for DMSO, merge their names
  # 1b) extract all GSMs for other
  # 1c) compare each 1b) to merged 1a)
  # !!! Not all samples from one comparison are from single series. 
  # Best to work with superseries - should be workable with simple search function of the browser
  
  opts_comparisons_76 <- list('input' = readr::read_tsv(file = paste0(opts$dir_r_downloaded_data, '/76_samples.txt')))
  opts_comparisons_76$input$Group <- NULL
  
  opts_comparisons_76$subset_logic_drug <- stringr::str_detect(string = tolower(opts_comparisons_76$input$Perturbagen), pattern = 'phenelzine|trazodone|tranylcypromine|bupropion|dmso')
  
  opts_comparisons_76$drug_subset <- subset(x = opts_comparisons_76$input, subset = opts_comparisons_76$subset_logic_drug)
  
  ### Here we change this to 3 logics: SZ, CN, cancerCells
  opts_comparisons_76$subset_logic_2_cell <- stringr::str_detect(string = tolower(opts_comparisons_76$drug_subset$`Cell id`), pattern = tolower('SH-SY5Y|2607-3-1|3084-2-2|3113-6-3|3121-2-1|3182-2-4|3234-2-4|3440-5-A|3651-A-A|4506-B-A|553-3-1|690-2-1|9319-2-A|BJ-2-A|2484-2-A|1275-B-3|2011-3-12|1835-1-5|2476-D1-2|676-2-3|2513-2-4|581-2-1|1792-1-E|2038-1-A|2497-1-C|449-2-12|2962-2-1'))
  
  opts_comparisons_76$drug_cell_subset <- subset(x = opts_comparisons_76$drug_subset, subset = opts_comparisons_76$subset_logic_2_cell)
  
  opts_comparisons_76$subset_logic_2_0nM <- stringr::str_detect(string = tolower( opts_comparisons_76$drug_cell_subset$Dosage), pattern = tolower('^0_uM'))
  opts_comparisons_76$subset_logic_2_0nM[is.na(opts_comparisons_76$subset_logic_2_0nM)] <- F
  
  opts_comparisons_76$drug_cell_0nM_subset <- subset(x = opts_comparisons_76$drug_cell_subset, subset = !opts_comparisons_76$subset_logic_2_0nM)
  
  opts_comparisons_76$subsets_to_use <- purrr::map(
    .x = unique(opts_comparisons_76$drug_cell_0nM_subset$`Source name`),
    .f = function(x){
      subset(opts_comparisons_76$drug_cell_0nM_subset, subset = opts_comparisons_76$drug_cell_0nM_subset$`Source name` == x) })
  names(opts_comparisons_76$subsets_to_use) <- unique(opts_comparisons_76$drug_cell_0nM_subset$`Source name`)
  
  length(opts_comparisons_76$drug_cell_0nM_subset[[1]]) == sum(purrr::map_int(.x = opts_comparisons_76$subsets_to_use, .f = function(x){ length(x[[1]]) } ))
  
  opts_comparisons_76$actual_comparisons_list <- purrr::map(
    .x = opts_comparisons_76$subsets_to_use, 
    .f = function(x) { 
      get_control_and_drug_pairs(x)
    })
  
  opts_comparisons_76$actual_comparisons_list <- rlist::list.flatten(opts_comparisons_76$actual_comparisons_list)
  
  return(opts_comparisons_76)
}
